byte_to_ascii:
    mov ah, 0                        
    mov bl, 10                      
    div bl                         
    add al, '0'                      ; Convert quotient (tens) to ASCII
    xchg al, ah                      ; Move tens to AH
    add al, '0'                   
    ret  

