print_lives_loop:
   
    mov ah, 0x0e         ; BIOS function to write character in teletype mode
    mov  al, 0x03      
    

    mov [es:di], al
    mov  word [es:di+1], 0x24  

    add di, 2           
    dec bl               
    jnz print_lives_loop

    pop di
    pop es
    pop bx
    pop ax
    ret

